package ll.negocio.agente;

import java.awt.image.BufferedImage;
import java.io.ByteArrayOutputStream;
import java.io.File;
import java.io.IOException;
import javax.imageio.ImageIO;
import javax.xml.bind.DatatypeConverter;

public class ImagenesNEG {

    private static ImagenesNEG _Instancia;

    private ImagenesNEG() {
    }

    public static ImagenesNEG Instancia() {
        if (_Instancia == null) {
            _Instancia = new ImagenesNEG();
        }
        return _Instancia;
    }

    public String fotosPersonal(String dni) throws Exception {

        String ruta = "F:\\Dropbox\\fotossanlorenzo\\" + dni + ".jpg";
        String result = null;
        try {

            BufferedImage bImage = ImageIO.read(new File(ruta));//give the path of an image
            ByteArrayOutputStream baos = new ByteArrayOutputStream();
            ImageIO.write(bImage, "jpg", baos);
            baos.flush();
            byte[] imageInByteArray = baos.toByteArray();
            baos.close();
            result = DatatypeConverter.printBase64Binary(imageInByteArray);

        } catch (IOException e) {
            result = "Error: " + e;
        }
        return result;
    }
    
    public String obtenerFotosSocio(String tipoFoto) throws Exception {        
        String foto="";
        String fotoImg64 = "";
        String  firmaImg64="";
        if(tipoFoto.equalsIgnoreCase("fotoSocio")){
            foto = fotoImg64;
        }
        if(tipoFoto.equalsIgnoreCase("firmaSocio")){
          foto=firmaImg64;
        }            
        return foto;
    }

}
